	/***get deduct values*/
	select (case when deduct_level1 is null then 0 else deduct_level1 end),
		   (case when deduct_level2 is null then 0 else deduct_level2 end),	
           (case when deduct_level3 is null then 0 else deduct_level3 end),	
		   (case when deduct_level4 is null then 0 else deduct_level4 end)
	into v_deduct_level1,v_deduct_level2,v_deduct_level3,v_deduct_level4 
	from t_deduct_regular limit 0,1;
	
	/*START TRANSACTION; */ 

    OPEN cursor_order;
	FETCH cursor_order INTO v_order_id,v_vip_id,v_order_date,v_product_id,v_quantity,v_price,v_amount;  
	REPEAT  	
	
		/* select * from t_product***/
		select special_deduct_flag, (case when deduct_price is null then 0 else deduct_price end) 
		into v_special_deduct_flag,v_deduct_price from t_product where id = v_product_id;
		
		if(v_special_deduct_flag=1)then
			select (case when deduct_level1 is null then 0 else deduct_level1 end),
				   (case when deduct_level2 is null then 0 else deduct_level2 end),	
				   (case when deduct_level3 is null then 0 else deduct_level3 end),	
