			select parent_id into v_p_vip_id_level4 from t_vip where id = v_p_vip_id_level3;	
		end if;
		
		if(v_deduct_price >0) then
			/*v_p_vip_id_level1*/
			if(v_p_vip_id_level1 is not null and v_deduct_level1 >0) then 
				call pr_recompute_vip_income_level(v_order_id,v_product_id,v_p_vip_id_level1,v_vip_id,v_deduct_price,v_deduct_level1,v_deduct_amount,p_description);
			end if;
			
			/*v_p_vip_id_level2*/
			if(v_p_vip_id_level2 is not null and v_deduct_level2>0) then 
				call pr_recompute_vip_income_level(v_order_id,v_product_id,v_p_vip_id_level2,v_vip_id,v_deduct_price,v_deduct_level2,v_deduct_amount,p_description);
			end if;
			
			/*v_p_vip_id_level3*/
			if(v_p_vip_id_level3 is not null and v_deduct_level3>0) then 
				call pr_recompute_vip_income_level(v_order_id,v_product_id,v_p_vip_id_level3,v_vip_id,v_deduct_price,v_deduct_level3,v_deduct_amount,p_description);
